import React from "react";
import { observable, isArrayLike, computed, action } from "mobx";
import { observer } from "mobx-react";
import "./DateSelect.scss";
import { History } from "history";
import { Header } from "../../index";
import { Calendar } from 'antd-mobile';

interface Props {
  history: History;
  today: number;
  selectDate: number;
}

const DateSelect = (props: Props) => {
  const { history, today, selectDate } = props;
  console.log(new Date(today), '000-11')
  return (
    <div className="date-page">
      <Header history={history} title={"选择日期"} />
      <div className="date-box">
        <Calendar
          visible={true}
          infiniteOpt={true}
          initalMonths={2}
          type={'one'}
          minDate={new Date(today)}
          maxDate={new Date(today + 3600 * 24 * 15 * 1000)}
          onConfirm={(e) => {
            console.log(e, '000-date')
          }}
        />
      </div>
      
    </div>
  );
};
export default observer(DateSelect);
