import React, { useState, useEffect } from "react";
import { toJS } from "mobx";
import { observer } from "mobx-react";
import "./City.scss";
import { History } from "history";
import { Header } from "../../index";
import index from "../../../store/index";
import { InputItem } from 'antd-mobile';

interface CityInfo{
  idx: string;
  cities: City[];
}

interface City{
  id: number;
  latitude: number;
  longitude: number;
  name: string;
  pinyin: string;
}

interface Props {
  history: History;
}

const City = (props: Props) => {
  const { history } = props;
  const [cityList, setCityList] = useState([]);
  
  useEffect(() => {
    const getCityList = async () => {
      let res = await index.getCityList();
      if (res.resultCode === 0) {
        setCityList(res.result);
      } 
    }
    getCityList();

  }, [])
  console.log(toJS(cityList), '000-cityList')
  return (
    <div className="city-page">
      <Header history={history} title={"选择城市"} />
      {/* <InputItem
        clear
        placeholder="请输入城市或车站名"
      /> */}
      {cityList.map((item: CityInfo) => {
        return <div className="cityList" key={item.idx}>
          <div className="cityCate">{item.idx}</div>
          {item.cities.map((cityItem: City) => {
            return <div className="cityName" key={cityItem.id}>{cityItem.name}</div>
          })}
        </div>
      })}
    </div>
  );
};
export default observer(City);
