import React, { useState, useEffect } from "react";
import { toJS } from "mobx";
import { observer } from "mobx-react";
import "./City.scss";
import { History } from "history";
import { Header } from "../../index";
import index from "../../../store/index";
import { SearchBar } from 'antd-mobile';
import { CityInfo, CityObj } from '../../../models/index';

interface Props {
  history: History;
}

const City = (props: Props) => {
  const { history } = props;
  const [cityList, setCityList] = useState([]);
  const [searchVal, setSearchVal] = useState('');
  const [citySearchList, setSearchCityList] = useState([]);
  
  useEffect(() => {
    const getCityList = async () => {
      let res = await index.getCityList();
      if (res.resultCode === 0) {
        setCityList(res.result);
        localStorage.setItem('cityList', JSON.stringify(res.result));
      } 
    }
    let cityArr = JSON.parse(localStorage.getItem('cityList') || '[]');
    if (cityArr.length){
      setCityList(cityArr);
    } else {
      getCityList();
    }
  }, [])

  useEffect(() => {
    const getSearchCityList = async () => {
      let res = index.getSearchCityList(searchVal);
      setSearchCityList(res);
    }
    getSearchCityList();
  }, [])

  console.log(searchVal, '000-cityList')
  return (
    <div className="city-page">
      <Header history={history} title={"选择城市"} />
      <SearchBar
        className="searchCity"
        placeholder="请输入城市或车站名"
        onClear={() => {}}
        onCancel={() => {

        }}
        onChange={(e) => {
          setSearchVal(e);
        }}
      />

      {citySearchList.length ? citySearchList.map((item: CityInfo) => {
        return <div className="cityItem" key={item.id}>
          {item.name}
        </div>
      }) : 
        <div className="emptyList">无法查询到车站</div>
      }

      {!searchVal && cityList.map((item: CityObj) => {
        return <div className="cityCate" key={item.idx}>
          <div className="cateName">{item.idx}</div>
          {item.cities.map((cityItem: CityInfo) => {
            return <div className="cityItem" key={cityItem.id} onClick={() => {index.changeStation(cityItem.name); history.goBack()}}>{cityItem.name}</div>
          })}
        </div>
      })}
    </div>
  );
};
export default observer(City);
